import UIKit

class LoginPresenter: LoginPresenterProtocol {
    weak private var view: LoginViewProtocol?
    var interactor: LoginInteractorInputProtocol?
    private let router: LoginWireframeProtocol

    init(interface: LoginViewProtocol, interactor: LoginInteractorInputProtocol?, router: LoginWireframeProtocol) {
        self.view = interface
        self.interactor = interactor
        self.router = router
    }
    
    func login(email: String, password: String) {
        view?.showLoading()
        if isValidLoginData(email: email, password: password) {
            interactor?.login(email: email, password: password)
            view?.dismissLoading()
        } else {
            view?.showError(with: "Please fill your email and password")
            view?.dismissLoading()
        }
    }
    
    func goToHome() {
        router.goToHome()
    }
    
    func goToRegister() {
        router.goToRegister()
    }
    
    func isValidLoginData(email: String, password: String) -> Bool {
        return !email.isEmpty && !password.isEmpty
    }
}

extension LoginPresenter: LoginInteractorOutputProtocol {
    func showLoginSucceeded() {
        view?.dismissLoading()
        view?.showLoginSucceeded()
    }
    
    func showError(with message: String) {
        view?.dismissLoading()
        view?.showError(with: message)
    }
}
